const Button = props => {
    // button styles
    const defaultStyles = {
        color: '#3F3F3F',
        backgroundColor: '#E0E0E0',
        border: 'none',
        fontSize: '20px',
        padding: "10px",
        borderRadius: '5px',
        '&:hover, &:focus': {
            background: '#000',
        }
    }


    return (
        <button style={defaultStyles}>
            {props.text}
        </button>
    )
}

export default Button
